mapper.select:


     and classify_name like concat('%', #{classifyName}, '%')
     and status = #{status}

order by parent_id, order_num

mapper.update:
update goods_classify
parent_id = #{parentId}, ancestors = #{ancestors}, classify_name = #{classifyName}, order_num = #{orderNum}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where classify_id = #{classifyId}

update goods_classify set ancestors =

    when #{item.classifyId} then #{item.ancestors}

,status =

    when #{item.classifyId} then #{item.status}

where classify_id in

    #{item.classifyId}
