mapper.select:
select a.serve_id,a.consult_id, b.nick_name, b.user_name, a.num,a.del_flag,a.status from psy_consult_serve a inner join psy_consult b on a.consult_id = b.id

     and a.consult_id = #{consultId}
     and a.serve_id = #{serveId}
     and a.del_flag = #{delFlag}
     and a.status = #{status}

select distinct a.consult_id,b.nick_name, b.user_name from psy_consult_serve a inner join psy_consult b on a.consult_id = b.id

     and a.consult_id = #{consultId}
     and a.serve_id = #{serveId}
     and a.del_flag = #{delFlag}
     and a.status = #{status}
