mapper.select:
SELECT
  *
FROM
    t_order
WHERE 1 = 1

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}


    AND order_id = #{orderId}


    AND order_type = #{orderType}


    AND order_status = #{orderStatus}

order by create_time desc , order_id desc
LIMIT ${(page-1)*limit},${limit}

SELECT
  count(1)
FROM
    t_order
WHERE 1 = 1

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}


    AND order_id = #{orderId}


    AND order_type = #{orderType}


    AND order_status = #{orderStatus}
