mapper.select:
SELECT id, `name`, group_source, `state`, create_time FROM t_play_info tpi WHERE 1 = 1
AND tpi.merchant_id = #{dto.merchantId}
AND tpi.create_time >= #{dto.startTime}
AND tpi.create_time <= #{dto.endTime}
AND tpi.state = #{dto.state}
AND tpi.name like concat('%',#{dto.queryValue},'%')
AND tpi.id = #{dto.queryValue}
order by tpi.create_time desc

SELECT tpmp.play_id, count( 1 ) AS totalNum, sum( IF ( tpmpd.send_state > 0, 1, 0 ) ) AS currentNum, sum( IF ( tpmpd.send_state = 1, 1, 0 ) ) AS sendSuccessNum, sum( IF ( tpmpd.send_state = 2, 1, 0 ) ) AS sendFailNum FROM t_play_message_push tpmp LEFT JOIN t_play_message_push_detail tpmpd ON tpmp.id = tpmpd.play_msg_push_id WHERE tpmp.play_id IN
#{playId}
GROUP BY tpmp.play_id

SELECT tpi.id AS playId, tpi.state, tpi.group_num AS groupTotalNum, sum( IF ( tgs.group_status = 1, 1, 0 ) ) AS groupFailNum, count( tpgi.group_id ) AS groupCurrentNum, count( tpmp.id ) AS packTotalNum, sum( IF ( tpmp.robot_pack_flag = 1, 1, 0 ) ) AS packCurrentNum, 100 AS joinGroupTotalNum, 100 AS joinGroupCurrentNum FROM t_play_info tpi LEFT JOIN t_play_message_push tpmp ON tpi.id = tpmp.play_id LEFT JOIN t_play_group_info tpgi ON tpi.id = tpgi.play_id LEFT JOIN t_group_state tgs ON tpmp.group_id = tgs.group_id WHERE tpi.id IN
#{playId}
GROUP BY tpi.id

SELECT
    tpmp.group_id,
    tgi.group_serial_no,
    tpmp.group_name,
    tpmpd.spokesman_nickname,
    tpmpd.robot_id,
    tpmpd.robot_nickname,
    tpmpd.robot_img_url,
    tpmpd.robot_acct,
    tpmpd.message_content,
    tpmpd.message_sort,
    tpmpd.send_state,
    tpmpd.send_time,
    tpmpd.create_time,
    tpmpd.push_fail_reason
FROM
    t_play_message_push tpmp
    LEFT JOIN t_play_message_push_detail tpmpd ON tpmp.id = tpmpd.play_msg_push_id
    LEFT JOIN t_group_info tgi ON tpmp.group_id = tgi.group_id
WHERE
    1 = 1
  AND tpmp.play_id = #{dto.playId}



            and tpmpd.send_time >= #{dto.startTime}


            and tpmpd.send_time <= #{dto.endTime}




            and tpmpd.create_time >= #{dto.startTime}


            and tpmpd.create_time <= #{dto.endTime}




    and tpmpd.send_state = #{dto.sendState}



    and tpmpd.robot_id = #{dto.robotId}


    and tpmpd.robot_acct = #{dto.robotAcct}


    and tpmpd.robot_nickname = #{dto.robotNickname}




    and tpmp.group_id = #{dto.groupId}


    and tpmp.group_name = #{dto.groupName}


    and tgi.group_serial_no = #{dto.groupSerialNo}


ORDER BY


        tpmpd.send_time


        tpmpd.create_time




        ASC


        DESC
