mapper.select:
SELECT tri.country_code AS countryCode, tri.country AS countryName, count( 1 ) AS robotTotalNUm, SUM( IF ( tri.seal_status = 10, 1, 0 ) ) AS robotSuccessNUm, SUM( IF ( tri.bidirectional_type = 1, 1, 0 ) ) AS bidirectionalNum, SUM( IF ( tri.seal_status != 10, 1, 0 ) ) AS closureNum, SUM( IF ( tri.heartbeat_status = 10, 1, 0 ) ) AS offlineNum, sum( t.sendTotalNUm ) AS sendTotalNUm, sum( t.sendSuccessNUm ) AS sendSuccessNUm, sum( t.sendFailNUm ) AS sendFailNUm FROM ( SELECT tpmpd.robot_id, count( tpmpd.id ) AS sendTotalNUm, SUM( IF ( tpmpd.send_state = 1, 1, 0 ) ) AS sendSuccessNUm, SUM( IF ( tpmpd.send_state = 2, 1, 0 ) ) AS sendFailNUm FROM t_play_message_push tpmp LEFT JOIN t_play_message_push_detail tpmpd ON tpmp.id = tpmpd.play_msg_push_id WHERE 1 = 1 AND tpmp.play_id = #{dto.playId} AND tpmpd.robot_id != '' GROUP BY tpmpd.robot_id UNION select tpbr.robot_id, 0 as 'sendTotalNUm', 0 as 'sendSuccessNUm', 0 as 'sendFailNUm' from t_play_back_robot tpbr where 1=1 and tpbr.is_finish = -1 and tpbr.play_id = #{dto.playId} ) t LEFT JOIN t_robot_info tri ON t.robot_id = tri.robot_serial_no WHERE 1 = 1
AND tri.country = #{dto.countryName}
GROUP BY tri.country

SELECT t.robot_id as robotSerialNo tri.phone, tri.country_code AS countryCode, tri.country AS countryName, tri.heartbeat_status as heartbeatStatus, tri.seal_status sealStatus, tri.bidirectional_type bidirectionalType, t.group_id groupId, tgi.group_serial_no groupSerialNo FROM ( SELECT tpmpd.robot_id,tpmp.group_id FROM t_play_message_push tpmp LEFT JOIN t_play_message_push_detail tpmpd ON tpmp.id = tpmpd.play_msg_push_id WHERE 1 = 1 AND tpmp.play_id = #{dto.playId} AND tpmpd.robot_id != '' GROUP BY tpmpd.robot_id UNION select tpbr.robot_id,tpbr.group_id from t_play_back_robot tpbr where 1=1 and tpbr.is_finish = -1 and tpbr.play_id = #{dto.playId} ) t LEFT JOIN t_robot_info tri ON t.robot_id = tri.robot_serial_no left join t_group_info tgi on tgi.group_id = t.group_id
and t.robot_id = #{dto.robotSerialNo}
and tri.phone = #{dto.phone}
and tri.country = #{dto.countryName}
and tgi.group_serial_no = #{dto.groupSerialNo}
and tri.heartbeat_status = #{dto.heartbeatStatus}
and tri.seal_status = #{dto.sealStatus}
and tri.bidirectional_type = #{dto.bidirectionalType}
